import React, { useEffect } from "react";
import { Helmet } from "react-helmet";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";
import AllProjects from "../components/projects/allProjects";

import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/projects.css";

const Projects = () => {
	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const currentSEO = SEO.find((item) => item.page === "projects");

	return (
		<React.Fragment>
			<Helmet>
				<title>{`Projects | ${INFO.main.title}`}</title>
				<meta name="description" content={currentSEO.description} />
				<meta
					name="keywords"
					content={currentSEO.keywords.join(", ")}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="projects" />
				<div className="content-wrapper">
					<div className="projects-logo-container">
						<div className="projects-logo">
							 
						</div>
					</div>
					<div className="projects-container">
						<div className="title projects-title">
							Some of the Cool Projects I have Worked On.
						</div>

						<div className="subtitle projects-subtitle">
						Throughout my journey, I've engaged in a diverse array of projects, each representing a significant milestone in my development. A considerable portion of these endeavors is open-source, inviting fellow enthusiasts to delve into the codebase and offer their insights and enhancements. Should any of these projects catch your interest, I warmly encourage you to peruse the code and propose any refinements or innovations you envision. Embracing collaboration as a catalyst for personal and collective advancement, I eagerly welcome fresh perspectives and constructive feedback, recognizing them as invaluable opportunities for continual learning and evolution.
						</div>

						<div className="projects-list">
							<AllProjects />
						</div>
					</div>
					<div className="page-footer">
						<Footer />
					</div>
				</div>
			</div>
		</React.Fragment>
	);
};

export default Projects;
